import {NEW_MESSAGE} from "../actions/types";

const DEFAULT_MESSAGES = {items: []};
const messagesReducer = (state = DEFAULT_MESSAGES, action) => {
  switch (action.type) {
    case NEW_MESSAGE:
      return {...state, items: [...state.items, action.item]};
    default:
      return state;
  }
};
export default messagesReducer;
